// Auto-generated registry component
"use client";

import React, { useState, useEffect } from "react";
import { startTransition } from 'react';
import Stories from "@/components/ui/stories";

interface Video {
  id: number;
  video: string;
  description: string;
  link: string | null;
}

interface Story {
  id: number;
  title: string;
  image: string;
  createdAt: string;
  updatedAt: string;
  videos: Video[];
}

const StoriesDemo: React.FC = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [selectedStoryIndex, setSelectedStoryIndex] = useState(0);
  const [isMobile, setIsMobile] = useState(() => typeof window !== 'undefined' ? window.innerWidth < 768 : false);

  useEffect(() => {
    const handleResize = () => {
      startTransition(() => {
        setIsMobile(window.innerWidth < 768);
      });
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const isLoading = false;
  const error = null;

  const storiesData = {
    results: [
      {
        id: 1,
        title: "Story 1",
        image: "/images/story/story-avatar-1.jpeg",
        created: "2025-01-01T00:00:00Z",
        modified: "2025-01-02T00:00:00Z",
        videos: [
          {
            id: 101,
            video: "/images/story/story-1.mp4",
            description: "Daily dose of cuteness: 1 duck, lots of joy ðŸ˜",
            link: null
          }
        ]
      }
    ]
  };
  const placeholderCount = isMobile ? 3 : 9;

  const stories: Story[] = storiesData ? storiesData.results.map(story => ({
    id: story.id,
    title: story.title,
    image: story.image,
    createdAt: story.created,
    updatedAt: story.modified,
    videos: story.videos
  })) : [];

  if (error) return null;
  if (!isLoading && stories.length === 0) return null;

  const handleStoryClick = (index: number) => {
    setSelectedStoryIndex(index);
    setModalOpen(true);
  };

  return (
    <>
      <div className="p-4 my-4 max-w-6xl xl:ml-6">
        <div className="flex flex-wrap gap-4">
          {stories.map((story, index) => (
            <div
              key={story.id}
              className="flex flex-col items-center cursor-pointer"
              onClick={() => handleStoryClick(index)}
            >
              <div className="p-[2px] rounded-full bg-gradient-to-tr from-yellow-500 via-red-400 to-purple-500">
                <div className="w-20 h-20 rounded-full overflow-hidden bg-white dark:bg-zinc-950">
                  <img
                    src={story.image}
                    alt={story.title}
                    width={84}
                    height={84}
                    className="object-cover"
                  />
                </div>
              </div>
              <h1 className="text-sm font-regular tracking-tighter mt-2 dark:text-neutral-200">
                {story.title}
              </h1>
            </div>
          ))}
        </div>
      </div>
      {isModalOpen && stories[selectedStoryIndex]?.videos && (
        <Stories
          onClose={() => setModalOpen(false)}
          stories={stories}
          initialStoryIndex={selectedStoryIndex}
        />
      )}
    </>
  );
};

export default StoriesDemo;

"use client";

import React, { useEffect, useRef, useState, MutableRefObject, useCallback } from \"react\";
import useEmblaCarousel from \"embla-carousel-react\";
import Fade from \"embla-carousel-fade\";
import { gsap } from \"gsap\";
import { IconChevronRight, IconChevronLeft, IconVolume, IconVolumeOff, IconLink } from '@tabler/icons-react';

interface Story {
  id: number;
  image: string;
  title: string;
  updatedAt: string;
  videos?: {
    id: number;
    video: string;
    description: string;
    link: string | null;
  }[];
}

interface FullScreenCarouselProps {
  onClose: () => void;
  stories: Story[];
  initialStoryIndex: number;
  origin?: { x: number; y: number } | null;
}

const getTimeAgo = (dateString: string) => {
  const date = new Date(dateString);
  const now = new Date();
  const seconds = Math.floor((now.getTime() - date.getTime()) / 1000);

  let interval = Math.floor(seconds / 31536000);
  if (interval >= 1) return interval + (interval === 1 ? " year ago" : " years ago");
  interval = Math.floor(seconds / 2592000);
  if (interval >= 1) return interval + (interval === 1 ? " month ago" : " months ago");
  interval = Math.floor(seconds / 86400);
  if (interval >= 1) return interval + (interval === 1 ? " day ago" : " days ago");
  interval = Math.floor(seconds / 3600);
  if (interval >= 1) return interval + (interval === 1 ? " hour ago" : " hours ago");
  interval = Math.floor(seconds / 60);
  if (interval >= 1) return interval + (interval === 1 ? " minute ago" : " minutes ago");
  return "Just now";
};

export default function FullScreenCarousel({ onClose, stories, initialStoryIndex, origin }: FullScreenCarouselProps) {
  const [emblaRef, emblaApi] = useEmblaCarousel({ loop: false, skipSnaps: false }, [Fade()]);
  const [currentIndex, setCurrentIndex] = useState(initialStoryIndex);
  const [isMuted, setIsMuted] = useState(true);
  const videoRefs = useRef<(HTMLVideoElement | null)[]>([]);
  const containerRef = useRef<HTMLDivElement>(null);

  // Animate origin on mount
  useEffect(() => {
    if (origin && containerRef.current) {
      const el = containerRef.current;
      const { x, y } = origin;
      gsap.fromTo(
        el,
        { x, y, scale: 0.4, opacity: 0 },
        { x: 0, y: 0, scale: 1, opacity: 1, duration: 0.5, ease: "power3.out" }
      );
    }
  }, [origin]);

  // Play the current video and pause others
  useEffect(() => {
    videoRefs.current.forEach((video, index) => {
      if (video) {
        if (index === currentIndex) {
          video.currentTime = 0;
          video.muted = isMuted;
          video.play().catch(() => {});
        } else {
          video.pause();
        }
      }
    });
  }, [currentIndex, isMuted]);

  // Embla event handlers
  useEffect(() => {
    if (!emblaApi) return;
    const onSelect = () => {
      setCurrentIndex(emblaApi.selectedScrollSnap());
    };
    emblaApi.on("select", onSelect);
    return () => {
      emblaApi.off("select", onSelect);
    };
  }, [emblaApi]);

  const scrollPrev = useCallback(() => {
    if (emblaApi) emblaApi.scrollPrev();
  }, [emblaApi]);

  const scrollNext = useCallback(() => {
    if (emblaApi) emblaApi.scrollNext();
  }, [emblaApi]);

  const toggleMute = () => {
    setIsMuted(!isMuted);
  };

  const currentStory = stories[currentIndex];

  return (
    <div
      ref={containerRef}
      className="fixed inset-0 z-50 flex flex-col bg-black text-white"
      style={{
        touchAction: 'pan-y'
      }}
    >
      <div className="flex items-center justify-between p-4">
        <button
          aria-label="Close"
          onClick={onClose}
          className="text-white hover:text-gray-300"
        >
          âœ•
        </button>
        <div className="flex items-center gap-4">
          <button
            aria-label={isMuted ? "Unmute video" : "Mute video"}
            onClick={toggleMute}
            className="text-white hover:text-gray-300"
          >
            {isMuted ? <IconVolumeOff size={24} /> : <IconVolume size={24} />}
          </button>
          {currentStory && currentStory.videos && currentStory.videos[currentIndex] && currentStory.videos[currentIndex].link && (
            <a
              href={currentStory.videos[currentIndex].link ?? "#"}
              target="_blank"
              rel="noopener noreferrer"
              className="text-white hover:text-gray-300"
              aria-label="Open link"
            >
              <IconLink size={24} />
            </a>
          )}
        </div>
      </div>

      <div className="flex-1 overflow-hidden" ref={emblaRef}>
        <div className="embla__container flex h-full">
          {stories.map((story, index) => (
            <div
              className="embla__slide relative flex-shrink-0 w-full h-full flex flex-col items-center justify-center"
              key={story.id}
            >
              {story.videos && story.videos.length > 0 ? (
                <video
                  ref={(el) => (videoRefs.current[index] = el)}
                  src={story.videos[0].video}
                  className="max-h-full max-w-full object-contain"
                  muted={isMuted}
                  playsInline
                  loop
                  controls={false}
                />
              ) : (
                <img
                  src={story.image}
                  alt={story.title}
                  className="max-h-full max-w-full object-contain"
                />
              )}
              <div className="absolute bottom-4 left-4 right-4 text-white bg-black bg-opacity-50 p-2 rounded">
                <h2 className="text-lg font-semibold">{story.title}</h2>
                <p className="text-sm">{getTimeAgo(story.updatedAt)}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="flex justify-between p-4">
        <button
          aria-label="Previous"
          onClick={scrollPrev}
          disabled={currentIndex === 0}
          className="text-white disabled:opacity-50 hover:text-gray-300"
        >
          <IconChevronLeft size={32} />
        </button>
        <button
          aria-label="Next"
          onClick={scrollNext}
          disabled={currentIndex === stories.length - 1}
          className="text-white disabled:opacity-50 hover:text-gray-300"
        >
          <IconChevronRight size={32} />
        </button>
      </div>
    </div>
  );
}
